import { useEffect, useState } from "react";

const UseLocalStorageState = (key, defaultValue) => {
  const [state, setState] = useState(() => {
    let value;
    try {
      value = JSON.parse(
        window.localStorage.getItem("data") || JSON.stringify(defaultValue)
      );
    } catch (e) {
      value = defaultValue;
    }
    return value;
  });

  useEffect(() => {
    let data = [];
    const result = data.concat(state?.result);
    result.map((value, key) => {
      if (value[key] !== value[key]) {
        return window.localStorage.setItem(key, JSON.stringify(result));
      }
    });
    console.log(result);
    // eslint-disable-next-line
  }, [state]);

  return [state, setState];
};

export default UseLocalStorageState;
