import { useState } from "react";

const UselocalStorage = (localItems) => {
  const [storeData, SetStoreData] = useState(localStorage.getItem(localItems));

  const setLoc = (newItem) => {
    localStorage.setItem(localItems, newItem);
    SetStoreData(newItem);
  };
  return [storeData, setLoc];
};
export default UselocalStorage;
