import { useState } from "react";
import ExcelDownloads from "./ExcelDownload";

import "./App.css";
import UseLocalStorageState from "./useLocalStorage";

const App = () => {
  const [phone, setPhone] = useState();
  const data = JSON.parse(localStorage.getItem("data"));
  const dataExcel = () => {
    return data?.map((res, index) => {
      res[index]?.result.map((phone, index) => {
        return {
          id: index,
          phone: phone,
        };
      });
    });
  };

  const handleChange = (event) => {
    setPhone(event.target.value);
  };

  const onSubmit = (e) => {
    e.preventDefault();
    fetch(`http://localhost:90/randomphonenumbers/?phone=${phone}`)
      .then((res) => res.json())
      .then((record) => UseLocalStorageState(record));
  };

  const renderTableData = () => {
    return data?.result?.map((phone, index) => {
      return (
        <tr key={index}>
          <td>{phone}</td>
        </tr>
      );
    });
  };

  const dataRender = dataExcel();

  return (
    <div className="App">
      <header className="App-header">
        <h1>Filtro para buscar telefonos</h1>
        <form onSubmit={onSubmit}>
          <label>Telefono: </label>
          <input type="number" onChange={handleChange} />
          <input type="submit" />
        </form>
        <ExcelDownloads data={dataRender} />
        <table id="phones">
          <thead>
            <tr>
              <th>Phone Number</th>
            </tr>
          </thead>
          <tbody>{renderTableData()}</tbody>
        </table>
      </header>
    </div>
  );
};

export default App;
