import { useEffect, useState } from "react";

const UseLocalStorageState = (key, defaultValue) => {
  const [state, setState] = useState(() => {
    let value;
    try {
      value = JSON.parse(
        window.localStorage.getItem("data") || JSON.stringify(defaultValue)
      );
    } catch (e) {
      value = defaultValue;
    }
    return value;
  });

  useEffect(() => {
    state?.result.map((value, key) => {
      if (!value[key]) {
        return window.localStorage.setItem(key, JSON.stringify(state?.result));
      }
      return window.localStorage.setItem(key, JSON.stringify(...state?.result));
    });
    // eslint-disable-next-line
  }, [state]);

  return [state, setState];
};

export default UseLocalStorageState;
