import { useState } from "react";
import ExcelDownloads from "./ExcelDownload";

import "./App.css";
import UseLocalStorageState from "./useLocalStorage";

const App = () => {
  const [phone, setPhone] = useState();

  const data = JSON.parse(localStorage.getItem("data")) ?? [];

  const dataExcel = () => {
    return data.map((res, index) => {
      return {
        id: index + 1,
        phone: res,
      };
    });
  };
  const handleChange = (event) => {
    setPhone(event.target.value);
  };

  const onSubmit = (e) => {
    e.preventDefault();

    fetch(`http://localhost:91/randomphonenumbers/?phone=${phone}`)
      .then((res) => res.json())
      .then((record) =>
        record.result.forEach((ele) => UseLocalStorageState(ele))
      );
  };

  const renderTableData = () => {
    return data.map((phone, index) => {
      return (
        <>
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{phone}</td>
          </tr>
        </>
      );
    });
  };

  const dataRender = dataExcel();
  return (
    <div className="App">
      <header className="App-header">
        <h1>Filtro para buscar telefonos</h1>
        <form onSubmit={onSubmit}>
          <label>Telefono: </label>
          <input type="number" onChange={handleChange} />
          <input type="submit" />
        </form>
        <ExcelDownloads data={dataRender} />
        {`Existen: ${
          JSON.parse(localStorage.getItem("data")).length ?? 0
        } filas de numero telefonicos.`}
        <table id="phones">
          <thead>
            <tr>
              <th>ID</th>
              <th>Phone Number</th>
            </tr>
          </thead>
          <tbody>{renderTableData()}</tbody>
        </table>
      </header>
    </div>
  );
};

export default App;
